import React from 'react';
import { useTranslation } from 'react-i18next';

const ProductCompliance = () => {
  const { t } = useTranslation();

  return (
    <div id="product-compliance-container">
      <h1>{t('productCompliance.title')}</h1>
      <p>{t('productCompliance.welcome')}</p>

      <section>
        <h2>{t('productCompliance.compliance_and_requirements.title')}</h2>
        <p>{t('productCompliance.compliance_and_requirements.description')}</p>
        <p>{t('productCompliance.compliance_and_requirements.evaluation')}</p>
        <p>{t('productCompliance.compliance_and_requirements.backoffice')}</p>
        <ul>
          {t('productCompliance.compliance_and_requirements.details', { returnObjects: true }).map((detail: string, index: number) => (
            <li key={index}>{detail}</li>
          ))}
        </ul>
      </section>

      <section>
        <h2>{t('productCompliance.non_compliance_consequences.title')}</h2>
        <p>{t('productCompliance.non_compliance_consequences.description')}</p>
        <ul>
          {t('productCompliance.non_compliance_consequences.sanctions', { returnObjects: true }).map((sanction: string, index: number) => (
            <li key={index}>{sanction}</li>
          ))}
        </ul>
      </section>

      <section>
        <h2>{t('productCompliance.communication_with_buyers.title')}</h2>
        <p>{t('productCompliance.communication_with_buyers.description')}</p>
        <ul>
          {t('productCompliance.communication_with_buyers.requirements', { returnObjects: true }).map((requirement: string, index: number) => (
            <li key={index}>{requirement}</li>
          ))}
        </ul>
      </section>

      <section>
        <h2>{t('productCompliance.platform_commitment.title')}</h2>
        <p>{t('productCompliance.platform_commitment.description')}</p>
      </section>

      <section>
        <h2>{t('productCompliance.compliance_responsibilities.title')}</h2>
        <ul>
          {t('productCompliance.compliance_responsibilities.participants', { returnObjects: true }).map((participant: string, index: number) => (
            <li key={index}>{participant}</li>
          ))}
        </ul>
      </section>

      {t('productCompliance.legal_and_regulatory_updates') && (
        <section>
          <h2>{t('productCompliance.legal_and_regulatory_updates.title')}</h2>
          <p>{t('productCompliance.legal_and_regulatory_updates.description')}</p>
        </section>
      )}
    </div>
  );
};

export default ProductCompliance;
